package com.nazeer.general;

public class ControlStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 1-10
		for (int i = 1; i <= 10; i++) {
			System.out.print(i + "\t");
		}
		System.out.println();
		// 10-1
		for (int i = 10; i >= 1; i--) {
			System.out.print(i + "\t");
		}
	}
}
